import Web3 from 'web3';
import Eth from 'web3-eth'
import NFTMarketplace from "../abis/NFTMarketplace.json";

let selectedAccount;
let erc721token;
let isInitialized = false;
let web3;
let address;

export const init = async () => {
    let provider = window.ethereum;

	if (typeof provider !== 'undefined') {
		try{
		    const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            selectedAccount = accounts[0];
            console.log(`Selected account is ${selectedAccount}`);
		}catch(error){
		    if (error.code === 4001) {
                console.log("User rejected request")
                 //window.location.href = "https://nft.howsimpl.com/user/dashboard";
                
          }
          console.log(error);
		}
			

		window.ethereum.on('accountsChanged', function (accounts) {
			selectedAccount = accounts[0];
			if(typeof selectedAccount == "undefined"){
			    //window.location.href = "https://nft.howsimpl.com/user/dashboard";
			}
			console.log(`Selected account changed to ${selectedAccount}`);
			
		});
        web3 = new Web3(provider);
        address = NFTMarketplace.networks['5777'].address;
        erc721token = new web3.eth.Contract(NFTMarketplace.abi, address)
        isInitialized = true;

    }
};

export const mintToken = async (data) =>{
    if(!isInitialized){
        await init()
    }
    let tx_hash = null;
    let token_id = 0;
    try{
        tx_hash = await erc721token.methods.mint(data).send({from: selectedAccount, gas: 1000000}).then(function (result) {
        console.log(result);
        let tx = result.transactionHash;
        return tx
        });
        console.log("Hash: " + tx_hash);
        let receipt = await web3.eth.getTransactionReceipt(tx_hash)
        .then(function (result) {
            return result
        });
        let topics = receipt['logs'][0]['topics'];
        let tokenIdHex = topics[3].toString(10);
        token_id = parseInt(tokenIdHex, 16)

    }catch(error){
        console.log(error);
    }
    return {'Hash': tx_hash, 'TokenID': token_id};
};

export const getBalance = async () =>{
    if(!isInitialized){
        await init()
    }
    return erc721token.methods.balanceOf(selectedAccount).call();
}

export const totalSupply = async () =>{
    if(!isInitialized){
        await init()
    }
    return erc721token.methods.totalSupply().call();
}

export const ownerOf = async (id) =>{
    if(!isInitialized){
        await init()
    }
    return erc721token.methods.ownerOf(id).call();
}

export const payEther = async (_id) =>{
    if(!isInitialized){
        await init()
    }
    const unit = require('ethjs-unit');

    let isTransaction = false;
   
    let price = await erc721token.methods.getPrice(_id).call();
    console.log(address);
    let eth_wei = unit.toWei(price, 'ether');    

    if (selectedAccount != null) {
        let txHash = null;
        try {
            
            txHash = await erc721token.methods.buy(_id).send({
                from: selectedAccount,
                value: eth_wei 
              });
            console.log(txHash) 
        } catch (error) {
            console.log(error.code)
            console.log(error)
        }
        isTransaction = true;
    }
    return isTransaction;
}
