import React, {Component} from "react";
import Web3 from 'web3';
import NFTMarketplace from '../src/contracts/NFTMarketplace.sol';

class App extends Component{
    async componentDidMount(){
        await this.loadWeb3;
        await this.loadBlockchainData;
    }

    async loadWeb3(){
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
          }
          else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
          } 
    }

    async loadBlockchainData(){
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()
        this.setState({ account: accounts[0] })

        const networkId = await web3.eth.net.getId()
        const networkData = NFTMarketplace.networks[networkId]
        if(networkData) {
            const contract = web3.eth.Contract(NFTMarketplace.abi, networkData.address)
            
            const result = await contract.methods.mint().call()

            console.log(result);
            
        }
    }
}

export default App;
