import React, {Component} from "react";
import Web3 from "web3";
import detectEthereumProvider from "@metamask/detect-provider";
import NFTMarketplace from "../abis/NFTMarketplace.json"

class App extends Component{

    async componentWillMount(){      
        const metamaskInstalled = typeof window.web3 !== 'undefined'
        if(metamaskInstalled) {
            await this.loadWeb3();
        }
        window.contract = await this.loadContract();  
    }

   async loadWeb3(){
        if(window.ethereum){
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if(window.web3){
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else{
            window.alert("NON-Ethereum browser detected")
        }
    }

    async callMint(){
        const minted = await window.contract.methods.mint("https...1").call();
        if(minted){
            console.log("Succesful")
        }
        else{
            console.log("Error")
        }
    }

    async loadContract(){
        return await new window.web3.eth.Contract('NFTMarketplace.json', '0x0549f3Fb5c832Ee7478B371f0721D396982817d0')
    }
    


    render(){
        return(
        <div>
            <h1>NFT Marketplace</h1>
        </div>       
        )
    }
}

export default App;
