import Web3 from 'C:\Users\root\Desktop\marketplace(demo)\node_modules\web3\lib\index.js';
import NFTMarketplace from "../abis/NFTMarketplace.json";

let selectedAccount;

export const init = async () => {
    let provider = window.ethereum;

        if(typeof provider !== 'undefined'){
            provider
                    .request({method: 'eth_requestAccounts'})
                    .then((accounts) => {
                        selectedAccount = accounts[0];
                        console.log("Selected account", accounts);
                        
                    })
                    .catch((err) => {
                        console.log(err);
                    });
                    window.ethereum.on('accountsChanged', function(accounts){
                        selectedAccount = accounts[0];
                        console.log("Selected account: ", accounts);

                    });
        }
        else{
            console.log("Error")
        }
        const web3 = new Web3(provider);
        console.log(web3.version.api)
       // const networkId = await web3.eth.net.getId();
        //const nftcontract = new web3.eth.Contract(NFTMarketplace.abi, NFTMarketplace.networks[networkID].address)
}
