import React, {useState} from 'react';
import {mintToken, getBalance } from './web3.js';

function App(){
    const [minted, setMinted] = useState(false);
    const [balance, setBalance] = useState(0);
    const [data, setData] = useState(null)

    const getData = (val) =>{
        setData(val.target.value)
    }

     const mint = (data) => {
	 	mintToken(data)
	 		.then((tx) => {
	 			console.log(tx);
	 			setMinted(true);
	 		})
	 		.catch((err) => {
	 			console.log(err);
			});
	 };

     const callBalance = () => {
        getBalance()
			.then((balance) => {
				setBalance(balance);
			})
			.catch((err) => {
				console.log(err);
			});
            
    };

    

    return <div className = "App">
        <form>
            <label>
                <input type = "text" onChange={getData}/>
            </label>
        </form>
        <button onClick={() => mint(data)}>Mint Token</button>
        {!minted ?(
            <p>Create a token</p>
        ):(
            <p>Token minted succesfully</p>
        
        )}
        <p>Your balance: {balance}</p>
        <button onClick={() => callBalance()}>Balance</button>
    </div>;
}

export default App;
