import Web3 from 'web3';
import NFTMarketplace from "../abis/NFTMarketplace.json";

let selectedAccount;
let erc721token;
let isInitialized;

export const init = async () => {
    let provider = window.ethereum;

        if(typeof provider !== 'undefined'){
            provider
                    .request({method: 'eth_requestAccounts'})
                    .then((accounts) => {
                        selectedAccount = accounts[0];
                        console.log("Selected account", accounts);
                        
                    })
                    .catch((err) => {
                        console.log(err);
                        return;
                    });
                    window.ethereum.on('accountsChanged', function(accounts){
                        selectedAccount = accounts[0];
                        console.log("Selected account: ", accounts);

                    });
        }
        else{
            console.log("Error")
        }
        const web3 = new Web3(provider);
        const address = NFTMarketplace.networks['5777'].address;
        erc721token = new web3.eth.Contract(NFTMarketplace.abi, address)
        isInitialized = true;
};

export const mintToken = async () =>{
    if(!isInitialized){
        await init()
    }
    else{
        return erc721token.methods.mint("https...1").send({from: selectedAccount});
    }
};
