import React, {useEffect, useState} from 'react';
import {mintToken, getBalance, init, totalSupply } from './web3.js';

function App(){
    const [balance, setBalance] = useState(0);
    const [data, setData] = useState(null);
    const [supply, setSupply] = useState(0);
    //const [id, setId] = useState(0);

    useEffect (() =>{
        init();
        callTotalSupply();
    });

    const getData = (val) =>{
        setData(val.target.value)
    }

    const callTotalSupply = () =>{
        totalSupply()
                .then((supply) =>{
                    setSupply(supply);
                })
                .catch((err) =>{
                    console.log(err)
                })
    }

     const mint = (data) =>{
	 	mintToken(data)
	 		.then((tx) => {
	 			console.log(tx);
	 		})
	 		.catch((err) => {
	 			console.log(err);
			});
	 };

     const callBalance = () => {
        getBalance()
			.then((balance) => {
				setBalance(balance);
			})
			.catch((err) => {
				console.log(err);
			});

    };

    

    return <div className = "App">
        <form>
            <label>
                <input type = "text" onChange={getData}/>
            </label>
        </form>
        <button onClick={() => mint(data)}>Mint Token</button>
        <p>Create a token</p>
        <p>Your balance: {balance}</p>
        <button onClick={() => callBalance()}>Balance</button>
        <p>Total supply: {supply}</p>
    </div>;
}

export default App;
