import React, {useEffect, useState} from 'react';
import { init, mintToken } from './web3.js';

function App(){
    const [minted, setMinted] = useState(false);


    return <div className = "App">
        {!minted ?(
            <button onClick={() => mint()}>Mint Token</button> 
        ):(
            <p>Token minted</p>
        
        )}
    </div>;
}

export default App;
