import React, {useState, useEffect} from 'react';
import {mintToken, getBalance } from './web3.js';

function App(){
    const [minted, setMinted] = useState(false);
    const [balance, setBalance] = useState(0);

    useEffect(async () =>{

    })

    function getData(val){
        console.log(val)
    }

     const mint = () => {
	 	mintToken()
	 		.then((tx) => {
	 			console.log(tx);
	 			setMinted(true);
	 		})
	 		.catch((err) => {
	 			console.log(err);
			});
	 };

     const callBalance = () => {
        getBalance()
			.then((balance) => {
				setBalance(balance);
			})
			.catch((err) => {
				console.log(err);
			});
    };

    

    return <div className = "App">
        <form>
            <label>
                <input type = "text" onChange={getData}/>
            </label>
            <input type = "submit" value ="Mint Token"/>
        </form>

        {!minted ?(
            <button onClick={() => mint()}>Mint Token</button> 
        ):(
            <p>Token minted</p>
        
        )}
        <p>Your balance: {balance}</p>
        <button onClick={() => callBalance()}>Balance</button>
    </div>;
}

export default App;
