import React, {Component} from "react";
import Web3 from 'Web3';
import NFTMarketplace from '../abis/NFTMarketplace.json';

class App extends Component{
    async componentDidMount(){
        await this.loadWeb3();
        await this.loadBlockchainData();
    }

    async loadWeb3(){
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
          }
          else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
          } 
          else{
              window.alert("Error")
          }
    }

    async loadBlockchainData(){
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()
        //const networkId = await web3.eth.net.getId()      
}

    async createNFT(){
        const web3 = window.web3   
        const abi = await NFTMarketplace.json
        const networkData = NFTMarketplace.networks['5777']
        if(networkData) {
        const contract = new web3.eth.Contract(abi, networkData.address)
        this.setState({contract})
        console.log(this.state.contract)

        const totalSupply = await contract.methods.totalSupply().call()
        this.setState({totalSupply})
        console.log(this.state.totalSupply) 
        }
};

     constructor(props){
         super(props);
         this.state = {
             account: " ",
             contract: null,
             totalSupply: 0
         }
     }

    render(){
        return(
            <div>
                <h1>NFTMarketplace</h1>
                <button onClick = {() => this.createNFT()}>Mint</button>
            </div>
        )
    }
}

export default App;
